const l=JSON.parse('{"key":"v-016b8834","path":"/back/springcloud/001-%E5%BE%AE%E6%9C%8D%E5%8A%A1%E4%B8%8ESpringCloud.html","title":"001-微服务与SpringCloud","lang":"zh-CN","frontmatter":{},"headers":[{"level":2,"title":"1-单体应用架构存在的问题","slug":"_1-单体应用架构存在的问题","link":"#_1-单体应用架构存在的问题","children":[]},{"level":2,"title":"2-什么是微服务？","slug":"_2-什么是微服务","link":"#_2-什么是微服务","children":[]},{"level":2,"title":"3-微服务架构面临的挑战","slug":"_3-微服务架构面临的挑战","link":"#_3-微服务架构面临的挑战","children":[]},{"level":2,"title":"4-微服务架构的相关概念","slug":"_4-微服务架构的相关概念","link":"#_4-微服务架构的相关概念","children":[]},{"level":2,"title":"5-SpringCloud","slug":"_5-springcloud","link":"#_5-springcloud","children":[]}],"git":{"createdTime":1688388067000,"updatedTime":1688641462000,"contributors":[{"name":"deer","email":"2772540969@qq.com","commits":3}]},"readingTime":{"minutes":8.42,"words":2525},"filePathRelative":"back/springcloud/001-微服务与SpringCloud.md","localizedDate":"2023年7月3日","excerpt":"<h1> 001-微服务与SpringCloud</h1>\\n<h2> 1-单体应用架构存在的问题</h2>\\n<p>一个归档包（比如war包）包含的所有功能的应用程序。在项目中我们通常将需求分为三个主要部分：数据库、服务端处理、前端展现。在业务发展初期，由于所有的业务逻辑在一个应用中，开发、测试、部署都还比较容易且方便。这种通常称为单体应用。</p>\\n<p>单体应用比较容易部署、测试，在项目初期，单体应用可以很好地稳定运行。然而，一般项目都会随着需求而不断的变化以及增加，越来越多的人加入到项目的开发团队，代码库也在飞速地膨胀。慢慢地，单体应用变得越来越臃肿，可维护性、灵活性逐渐降低，维护成本越来越高。</p>"}');export{l as data};
